import axios from 'axios';

const API_KEY = "530447506d32114d727a10739c02bf82"; // Your TMDB API v3 key
const TOKEN = "eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI1MzA0NDc1MDZkMzIxMTRkNzI3YTEwNzM5YzAyYmY4MiIsInN1YiI6IjY3Yjc0ZTAwZTQ4NGZjMjE1NDFhMjgzNCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.hsFDCb1mm3rK8Vk0Z13GQ_JV-IDSX51L0lkt64aMX0Y";

const instance = axios.create({
    baseURL: 'https://api.themoviedb.org/3',
    params: {
        api_key: API_KEY,
    },
    headers: {
        'Authorization': `Bearer ${TOKEN}`,
        'Content-Type': 'application/json;charset=utf-8',
    },
});

export const requests = {
    fetchTrending: `/trending/all/week?language=en-IN`,
    fetchNetflixOriginals: `/discover/tv?with_networks=213&language=en-IN`,
    fetchTopRated: `/movie/top_rated?region=IN&language=en-IN`,
    fetchActionMovies: `/discover/movie?with_genres=28&language=en-IN`,
    fetchComedyMovies: `/discover/movie?with_genres=35&language=en-IN`,
    fetchHorrorMovies: `/discover/movie?with_genres=27&language=en-IN`,
    fetchRomanceMovies: `/discover/movie?with_genres=10749&language=en-IN`,
    fetchDocumentaries: `/discover/movie?with_genres=99&language=en-IN`,
};

export default instance;
